void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = mod(iTime,2e4)*.05;
    float xInv = 1./iResolution.x;
    vec2 G = (floor(gl_FragCoord.xy/vec2(3.,5.))*xInv);
    vec2 M = vec2(0.2472136,.56);
    //vec2 M = (iMouse.xy-iResolution.xy*.5)*xInv;
    vec2 U = (gl_FragCoord.xy-iResolution.xy*.5);
    float s = fract(sin(dot(G,vec2(37,97)))*4e5);
    s += (dot(U,M)); 
    s += time; 
    s = fract(s);

    vec4 tex = texture(iChannel0, fragCoord.xy/iResolution.xy);
    float gTex = (tex.r+tex.g+tex.b)*.333;
    fragColor = vec4(round(gTex+s-.5));
    //fragColor = vec4(round(s));
}
