void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.xy/iResolution.xy);

    float frequency = 8.0;

    //uv.y += sin((iTime + uv.x)*frequency)/200.0;
    //uncomment this for fun wiggly!
    uv.x = ((uv.x - 0.5)/(uv.y + ((1.0 - uv.y)/3.0))) + 0.5;

    float square = texture(iChannel0, uv).r - 0.5;
    vec3 noise = texture(iChannel1, vec2(uv.y, uv.x + iTime*0.1)).rgb;

    square = 1.0 - (abs(square) * 2.0);

    square *= noise.r * 2.0;


    square = step(square, uv.y + ((1.0 - uv.y)/3.0));

    vec3 squarevec = vec3 (square, square, square);
    vec3 tunnels = 1.0 - squarevec.rgb;
    squarevec.rgb *= texture(iChannel2, uv).rgb;
    tunnels.rgb *= texture(iChannel3, uv).rgb;
    squarevec += tunnels*tunnels;

    //squarevec.r += (sin(iTime) + 0.5)/7.0;
    //and uncomment this for a fun evil red effect.

    fragColor = vec4(squarevec, 1.0);
}

//iChannel0 is the texture that determines the tunnel layout
//iChannel1 changes the pattern that shifts the tunnels over time
//iChannel2 and iChannel3 are the textures of the dirt and tunnel backgrounds respectively
