Hit march(vec3 eye, vec3 marchingDirection) {
	const float precis = 0.001;
    float t = 0.0;
	float l = 0.0;
    for(int i=0; i<MAX_MARCHING_STEPS; i++){
	    Hit hit = world( eye + marchingDirection * t );
        if( hit.dist < precis ) return Hit(t, hit.matID, hit.normal);
        t += hit.dist * .5;
    }
    return miss;
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        world(vec3(p.x + EPSILON, p.y, p.z)).dist - world(vec3(p.x - EPSILON, p.y, p.z)).dist,
        world(vec3(p.x, p.y + EPSILON, p.z)).dist - world(vec3(p.x, p.y - EPSILON, p.z)).dist,
        world(vec3(p.x, p.y, p.z  + EPSILON)).dist - world(vec3(p.x, p.y, p.z - EPSILON)).dist
    ));
}

float orenNayarDiffuse(
  vec3 lightDirection,
  vec3 viewDirection,
  vec3 surfaceNormal,
  float roughness,
  float albedo) {

  float LdotV = dot(lightDirection, viewDirection);
  float NdotL = dot(lightDirection, surfaceNormal);
  float NdotV = dot(surfaceNormal, viewDirection);

  float s = LdotV - NdotL * NdotV;
  float t = mix(1.0, max(NdotL, NdotV), step(0.0, s));

  float sigma2 = roughness * roughness;
  float A = 1.0 + sigma2 * (albedo / (sigma2 + 0.13) + 0.5 / (sigma2 + 0.33));
  float B = 0.45 * sigma2 / (sigma2 + 0.09);

  return albedo * max(0.0, NdotL) * (A + B * s / t) / PI;
}

float gaussianSpecular(
  vec3 lightDirection,
  vec3 viewDirection,
  vec3 surfaceNormal,
  float shininess) {
  vec3 H = normalize(lightDirection + viewDirection);
  float theta = acos(dot(H, surfaceNormal));
  float w = theta / shininess;
  return exp(-w*w);
}

vec3 doLighting(in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec3 mal){
    vec3 lin = vec3(0.0);

    // key light
    //-----------------------------
    vec3  view = normalize(-rd);
    const vec3 lig1 = normalize(vec3(-5., 5., 5.));
	const vec3 lig2 = normalize(vec3(5., -5., 0.));

    float spc1 = gaussianSpecular(lig1, view, nor, 0.95)*0.5;
    float dif1 = max(0., orenNayarDiffuse(lig1, view, nor, -20.1, 1.0));
    vec3  col1 = vec3(2.,4.2,4.) * .75;
    lin += col1*spc1+dif1*col1;

    float spc2 = gaussianSpecular(lig2, view, nor, 0.95);
    float dif2 = max(0., orenNayarDiffuse(lig2, view, nor, -20.1, 1.0));
    vec3  col2 = vec3(2., .05, .15) * 1.;
    lin += col2*spc2+dif2*col2;

    // ambient light
    //-----------------------------
    lin += vec3(0.05);


    // surface-light interacion
    //-----------------------------
    vec3 col = mal*lin;

    return col;
}

