float Noise(in vec3 p){
    vec3 i = floor(p);
        vec3 f = fract(p); 
        f *= f * (3.0-2.0*f);

    return mix(
                mix(mix(hash13(i + vec3(0.,0.,0.)), hash13(i + vec3(1.,0.,0.)),f.x),
                        mix(hash13(i + vec3(0.,1.,0.)), hash13(i + vec3(1.,1.,0.)),f.x),
                        f.y),
                mix(mix(hash13(i + vec3(0.,0.,1.)), hash13(i + vec3(1.,0.,1.)),f.x),
                        mix(hash13(i + vec3(0.,1.,1.)), hash13(i + vec3(1.,1.,1.)),f.x),
                        f.y),
                f.z);
}

const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 ) * 1.7;

float FBM( vec3 p ){
    float f;

        f = 0.5000 * Noise(p); p = m*p;
        f += 0.2500 * Noise(p); p = m*p;
        f += 0.1250 * Noise(p); p = m*p;
        f += 0.0625   * Noise(p); p = m*p;
        f += 0.03125  * Noise(p); p = m*p;
        f += 0.015625 * Noise(p);
    return f;
}

float hash( float n ) { return fract(sin(n)*123.456789); }

float noise( in vec3 p )
{
    vec3 fl = floor( p );
    vec3 fr = fract( p );
    fr = fr * fr * ( 3.0 - 2.0 * fr );

    float n = fl.x + fl.y * 157.0 + 113.0 * fl.z;
    return mix( mix( mix( hash( n +   0.0), hash( n +   1.0 ), fr.x ),
                     mix( hash( n + 157.0), hash( n + 158.0 ), fr.x ), fr.y ),
                mix( mix( hash( n + 113.0), hash( n + 114.0 ), fr.x ),
                     mix( hash( n + 270.0), hash( n + 271.0 ), fr.x ), fr.y ), fr.z );
}

float fbm( in vec2 p, float t )
{
    float f;
    f  = 0.5000 * noise( vec3( p, t ) ); p *= 2.1;
    f += 0.2500 * noise( vec3( p, t ) ); p *= 2.2;
    f += 0.1250 * noise( vec3( p, t ) ); p *= 2.3;
    f += 0.0625 * noise( vec3( p, t ) );
    return f;
}

float fbm1x(float x, float time){
	float amplitude = 1.;
    float frequency = 1.;
    float y = sin(x * frequency);
    float t = 0.01*(-time * 130.0);
    y += sin(x*frequency*2.1 + t)*4.5;
    y += sin(x*frequency*1.72 + t*1.121)*4.0;
    y += sin(x*frequency*2.221 + t*0.437)*5.0;
    y += sin(x*frequency*3.1122+ t*4.269)*2.5;
    y *= amplitude*0.06;
    return y;
}

vec2 shiftAtPos(float x, float time){
	return vec2(fbm1x(x, time), fbm1x(x + 78.233, time));
}

vec2 rotate( in vec2 uv, float a)
{
    float c = cos( a );
    float s = sin( a );
    return vec2( c * uv.x - s * uv.y, s * uv.x + c * uv.y );
}

vec2 voronoi(in vec3 x){
    vec3 p = floor( x );
    vec3 f = fract( x );

	float id;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ )
    	for( int j=-1; j<=1; j++ )
    		for( int i=-1; i<=1; i++ ){
                vec3 b = vec3( float(i), float(j), float(k) );
                vec3 r = vec3( b ) - f + hash33( p + b );
                float d = dot( r, r );

                if( d < res.x ){
                    id = dot(p + b, vec3(20.31, 517., 113.));
                    res = vec2(d, res.x);
                }else if(d < res.y){
                    res.y = d;
                }
    }
    return vec2(res.x, abs(id));
}

float height(vec3 p){
	vec4 h;
    h.xy = voronoi(p * 3.);
    h.z = FBM(10. * p);
    h.w = FBM(3. * p);
    float craterSize = mix(.01, .1, h.y * .001);
    return - h.w * .3 + h.z * .04
           + mix(-pow(smoothstep(craterSize * 2., craterSize, h.x), 16.) * .005,
                  pow(smoothstep(craterSize, 0., h.x), .75) * .1, smoothstep(craterSize + .005, craterSize - .005, h.x));
}

#define EPSILON 0.01
vec3 normal(vec3 p) {
    return normalize(vec3(
        height(vec3(p.x + EPSILON, p.y, p.z)) - height(vec3(p.x - EPSILON, p.y, p.z)),
        height(vec3(p.x, p.y + EPSILON, p.z)) - height(vec3(p.x, p.y - EPSILON, p.z)),
        height(vec3(p.x, p.y, p.z + EPSILON)) - height(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir ) {
    if (iFrame > 1) discard;
    fragColor -= fragColor;
    fragColor.x = height(rayDir);
    fragColor.gba = normal(rayDir);
}
