/*
 * Use your mouse to light up the image
 */

float disk(vec2 r, vec2 center, float radius) {
	return 1. - smoothstep( radius, radius+.2, length(r-center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (fragCoord / iResolution.xy);
	vec2 r =  vec2(fragCoord.xy+0.5/iResolution.xy)/iResolution.y-0.5;
	float xMax = iResolution.x/iResolution.y;

    //Vars
    vec2 center;

    // background color depends on the x coordinate of the cursor
	vec3 bgCol = vec3(0,0,0);

	vec3 ret = bgCol;

    center = vec2(iMouse.xy-0.5 / iResolution.xy)/iResolution.y-0.5;
    ret = vec3(0,0,0);

    vec3 mixed = mix(ret, texture(iChannel0, p).xyz, disk(r, center, 0.1));
	//ret = smoothstep(mixed -0.1, mixed + 0.1, mixed;x);
    ret = mixed;
    vec3 pixel = ret;
    // Output to screen
    fragColor = vec4(pixel,1.0);
}
