vec3 cicle(vec2 pt, vec2 center, float r)
{
    vec3 ret = vec3(0.0, 0.0, 0.0);
	float dis = distance(pt, center);
    if (dis < r) {
    	ret = vec3(1.0, 1.0, 1.0);
    }
    return ret;
}

vec3 rect(vec2 pt, vec2 center, float r)
{
    vec3 ret = vec3(0.0, 0.0, 0.0);
	float disx = abs(pt.x - center.x);
    float disy = abs(pt.y - center.y);
    if (disx * 2.0f < r && disy * 2.0f < r) {
       ret = vec3(1.0, 1.0, 0.0);   
    }

    return ret;       

}

vec3 drawSin(vec2 pt)
{
    vec3 ret = vec3(0.0, 0.0, 0.0);
    float si = sin((pt.x + iTime / 10.0f) * 3.14f * 4.0f) * 0.5f;
    if (pt.y - 0.5 < si) {
         ret = vec3(1.0, 1.0, 1.0);
    }
    return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 tcol = texture(iChannel0, uv).rgb;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 center = vec2(0.5, 0.5);
    vec3 c = cicle(uv, center, 0.2f);
    vec3 r = rect(uv, center, 0.3f); 
    vec3 s = drawSin(uv);
    vec3 ns = vec3(1.0, 1.0, 1.0) - s;

    vec3 col = c * 0.4 + r *0.6;


    // Output to screen
    fragColor = vec4(s * tcol + ns * vec3(1.0, 1.0, 0.0) ,1.0);
}
