void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    if (abs(uv.y - sin(uv.x*4.0+9.0+iTime*2.0)/3.0) > 0.1
        && abs(uv.x - sin(uv.y*4.0+2.0+iTime*2.0)/3.0) > 0.1
        && abs(uv.x+uv.y - sin(uv.y*4.0+iTime*2.0)/3.0) > 0.1
    	&& abs(uv.x+uv.y - cos(uv.x*4.0+iTime*2.0)/3.0) > 0.1
    	&& abs(uv.x-uv.y - sin(uv.x*4.0+1.0+iTime*2.0)/3.0) > 0.1
       	&& abs(uv.x-uv.y - sin(uv.y*7.0+1.0+iTime*2.0)/3.0) > 0.1) {
     	col = vec3(0.0,0.0,0.0); 
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
