// Got help from Josh Bogin
// Change these
// ------
float columns = 5.0;
float rows = 5.0;
// ------
vec3 EvenOdd;
vec3 CheckerBoard;
vec3 Red = vec3(1.0,0.0,0.0);
vec3 Blue = vec3(0.0,0.0,1.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= columns;
    uv.y *= rows;

    // This stores the modulus of the sum of uv.x and uv.y
    // To position the square
	EvenOdd = vec3(mod(floor(uv.x) + floor(uv.y),2.0));

    // This is used to psotiion the squares next to each other
    uv = fract(uv);

    // This stores the colors and the positions of the squares
	CheckerBoard = Red * EvenOdd + Blue * (1.0 - EvenOdd);
    fragColor = vec4(CheckerBoard,1.0);
}
