void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	// normalize coordinates and move origin to center
	vec2 st = (fragCoord.xy / iResolution.xy) - vec2(0.5);

	// animate global scale: zoom in and out
	st *= cos(iTime * 0.5);

	// animate point distance from center
	vec2 offset = (0.03 + pow(sin(iTime * 0.5), 4.5)) * vec2(0.5, 0.0);

	// calculate gradient colors for two diametrically opposite points
	float c1 = distance(st, offset); float c2 = 0.95 - distance(st, -offset);

	// set output color as combination of both colors
	fragColor = vec4(vec3(pow(c1 + c2, 20.0)), 1.0);
}
