float calc(vec2 uv, mat2 m, vec2 off)
{
    uv *= 8.0;
    uv += off;

    uv *= m;

    vec2 a = round(uv);
    return smoothstep(0.2, 0.9, length(uv - a));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)

    mat2 mIdent = mat2(1, 0, 0, 1);
    mat2 mRot1 = mat2(0.85, 0.52,  -0.52, 0.85);
    mat2 mRot2 = mat2(0.45, 0.88,  -0.88, 0.45);

    vec2 uv = fragCoord/iResolution.xy;
    float col1 = calc(uv, mIdent, 0.3 * vec2(iTime * -3.0, iTime * 4.0));
    float col2 = calc(uv, mRot1, 0.3 * vec2(iTime * 2.0, iTime * -3.0));
    float col3 = calc(uv, mRot2, 0.3 * vec2(iTime * -1.0, iTime * 2.0));

    float col = min(min(col1, col2), col3);

    // Output to screen
    fragColor.rgb = vec3(0.2, 0.7, 0.7) + vec3(col) * 0.5;
}
