/*
	Self-designing cloth.
	Adjusts spring constants according to strain

*/


void mainImage( out vec4 Q, in vec2 U )
{
    Q = sin(B(U));

}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4
        b = B(U),
        nb = B(U+vec2(0,1)),
        eb = B(U+vec2(1,0)),
        sb = B(U-vec2(0,1)),
        wb = B(U-vec2(1,0)),
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    Q = mix(Q,0.25*(n+e+s+w),vec4(.0,.0,.3,.3));
    Q.zw += 0.25*(f(Q,b,n,nb)+f(Q,b,e,eb)+f(Q,b,s,sb)+f(Q,b,w,wb));
    Q.zw += G*(1.+b.x)*vec2(0,-1);
    Q.zw *= 0.995;
    Q.xy += Q.zw;

    if (Q.x<0.||iFrame < 1||U.x<2.||U.y<2.||R.x-U.x<2.||R.y-U.y<2.)
        Q = vec4(U,0,0);

}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4
        b = B(U),
        nb = B(U+vec2(0,1)),
        eb = B(U+vec2(1,0)),
        sb = B(U-vec2(0,1)),
        wb = B(U-vec2(1,0)),
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    float S = 0.25*(
        length(f(Q,b,n,nb))+
        length(f(Q,b,e,eb))+
        length(f(Q,b,s,sb))+
        length(f(Q,b,w,wb)));
    b.x += E*(S/b.x-Z);
    b.x = clamp(b.x,0.01,1.);
    b.y = S;
    b.x = mix(b.x,0.25*(nb+eb+sb+wb).x,.01);
    Q=b;
    if (iMouse.z>0.&&length(U-iMouse.xy)<30.) Q.x = .3;
	if (iFrame < 1||U.x<2.||U.y<2.||R.x-U.x<2.||R.y-U.y<2.) Q = vec4(1,0,0,0);
}

// E is the rate it evolves the spring force
// G is gravity 
// Z is the threshold for increasing or decreasing

#define E 7e-4
#define G 1e-3
#define Z .6


#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define t (float (iFrame) / 10000.)
vec2 f (vec4 a, vec4 ab, vec4 b, vec4 bb) {
    vec2 u = a.xy-b.xy;
    if (length(u)<1e-6) return vec2(0);
    return ab.x*bb.x*(1.-length(u))*normalize(u);
}

