const float pi = 3.141592653;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - vec2(1.0);
    uv.y /= iResolution.x / iResolution.y;

    uv *= 7.8;
    uv += vec2(5.5, -2.36);
    vec2 ruv = vec2(cos(uv.x) - sin(uv.y), sin(uv.x) + cos(uv.y));

    float time = iTime * .5 + 4.71;
    float l_thick = cos(time - pi / 2.) * 0.5;
    float freq = 102.0 + sin(time) * 100.;

    float t = cos((ruv.x - ruv.y) * freq) + 1. - l_thick;
    t = smoothstep(1., 0.9, t);

    vec3 col = mix(vec3(0.2, 0.025, 0.3), vec3(0.7, 0.1, 0.17), t);
    fragColor = vec4(col, 1.0);
}
