// Fork of "reciprocal zoom moire" by khlorghaal. https://shadertoy.com/view/4tcfW7
// 2020-09-25 03:57:29

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Lower values of scaleFactor zoom in, higher values zoom out.
    float scaleFactor = 16000.;
    float s = sin(uv.x*uv.y*scaleFactor);
    vec3 col = vec3(s);

    fragColor = vec4(col,1.0);
}
