const float pi = 3.14159;

vec3 tex(vec2 uv)
{
	float rot = atan(uv.y, uv.x) / pi + 1.0;
	rot += sin(iTime * 0.25) * 0.2;
	float t = sin(length(uv) * 10.0 + iTime + sin(rot * pi * 10.0));
	#if 0
	t = step(t, 0.5);
    #else // alternate the movement of the different colored cogs
	t = step(t, sin(iTime * 1.5) * 0.5 + 0.5);
    #endif
	return mix(vec3(.0, .4, .7), vec3(.0, .8, .8), t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - vec2(1.0);
    uv.y /= iResolution.x / iResolution.y;
    fragColor = vec4(tex(uv), 1.0);
}
