void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Constants to vary
    float C = 2.1+2.*cos(iTime*2.);
    float scale = 10.;
    float lineThickness = 0.2;

    //Coordinate transformation from pixels
    vec2 uv = scale*(2.*fragCoord-iResolution.xy)/iResolution.y;

    //Implicit function
    float f = tan(uv.x)*tan(uv.y)*C-1.;

    //Function plot
    fragColor = vec4(abs(f)>=lineThickness);

    //Coordinate system
    if(abs(uv.x)<0.05) fragColor = vec4(0);
    if(abs(uv.y)<0.05) fragColor = vec4(0);
    if(length(uv-vec2(1,0))<0.2) fragColor = vec4(0);
    if(length(uv-vec2(0,1))<0.2) fragColor = vec4(0);
}
