void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 p = textureLod(iChannel0,uv,0.0);

    float h = 0.0025;

    //Central difference
    vec4 fwd = textureLod(iChannel0,(uv+0.5*h),0.0);
    vec4 back = textureLod(iChannel0,(uv-0.5*h),0.0);

	vec4 dp = fwd-back;

    vec4 col = dp + dot(p,dp);
    // Output to screen
    fragColor = col;
}
