// playing with the equations in
// https://en.wikipedia.org/wiki/Stereographic_projection

#define G 3.
#define R iResolution
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*2./R.y - vec2(R.x/R.y, 1.);
    float d = dot(uv,uv);
    vec3 s = floor(vec3(2.*uv, d-1.)*G/(1.+d)+iTime);
    float v= fract((s.x+s.y)*.5)*2.;
    fragColor = vec4(vec3(v),1.0);
}
