void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //circle section
    float row = fragCoord.y - (iResolution.y / 2.0f) ;
    float column = fragCoord.x - (iResolution.x / 2.0f) + 8.0f * cos(iTime * 4.0f * (fragCoord.x / iResolution.x));

    float dist = sqrt((row * row) + (column * column)); 
    float maxdist = sqrt((iResolution.y * iResolution.y * 0.25f) + (iResolution.x * iResolution.x  * 0.25f));

    float otpt = (sin(((dist / maxdist) * 20.0f) + iTime * 6.0f) + 1.0f) / 2.0f;
	vec3 col = vec3(otpt, otpt, otpt);
    //circle section

    //checker section
    int r = int(((fragCoord.y + 1000.0f) / 10.0f) + 10.0f * sin(iTime * 3.0f + (fragCoord.y / iResolution.y) * 10.f));
    int c = int(((fragCoord.x + 1000.0f) / 10.0f) + 8.0f * cos(iTime * 4.0f + (fragCoord.x / iResolution.x) * 10.f));

    if(r % 2 == c % 2){
		col.rgb *= 1.0f;
    }else{
        col.rgb *= 0.0f;
    }
    //checker section

    // Output to screen
    fragColor = vec4(col, 1.0);
}
