void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 coordinates = fragCoord/iResolution.xy;

    const float blurSize = 25.0;
    const int range = int(floor(blurSize/2.0));

    vec4 colors = vec4(0);

    for (int x = -range; x <= range; x++) {
        for (int y = -range; y <= range; y++) {
            vec4 color = texture(iChannel0, coordinates+vec2(float(x)/iResolution.x,
                                                             float(y)/iResolution.y));
            colors += color;
        }
    }

    vec4 finalColor = colors/pow(blurSize,2.);
    fragColor = finalColor;
}
