// Palette
void color(in float scale, out vec3 col)
{
    if(scale <= .2)
        col = mix(vec3(0.05,0.05,0.05), vec3(0.05,0.05,0.05), scale*5.);
    else if(scale <= .4)
        col = mix(vec3(0.05,0.05,0.05), vec3(0.95,0.93,0.83), (scale-.2)*5.);
    else if(scale <= .6)
        col = mix(vec3(0.95,0.93,0.83), vec3(0.96,0.21,0.19), (scale-.4)*5.);
    else if(scale <= .8)
        col = mix(vec3(0.96,0.21,0.19), vec3(0.05,0.05,0.05), (scale-.6)*5.);
    else col = mix(vec3(0.05,0.05,0.05), vec3(0.05,0.05,0.05), (scale-.8)*5.);
}

const float fsaa = 128.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    float bound = sqrt(fsaa)-1.;
   	for(float i = -.5*bound; i<=.5*bound; i+=1.)
        for(float j=-.5*bound; j<=.5*bound; j+=1.)
        {
	        vec4 state = texture(iChannel0, fragCoord/iResolution.xy+3.*vec2(i,j)/max(bound, 1.)/iResolution.xy);
     		vec3 cola;
            color(sqrt(abs(state[0]/state[1])), cola);
            col += cola;
        }
    col /= fsaa;
    fragColor = vec4(col,1.0);
}

