//runge kutta params
float h = 1.e-2;

//math params
float PI = acos(-1.);

/* Evaluate system of differential equations
 * params:
 * tp[0]: theta1
 * tp[1]: theta2
 * tp[2]: ptheta1
 * tp[3]: ptheta2
 */
vec4 eval(in vec4 tp)
{
    float p0w = (L1*L2*(M1+M2*pow(sin(tp[0]-tp[1]),2.)));
    float C1 = tp[2]*tp[3]*sin(tp[0]-tp[1])/p0w;
    float C2 = (L2*L2*M2*tp[2]*tp[2]+L1*L1*(M1+M2)*tp[3]*tp[3]-L1*L2*M2*tp[2]*tp[3]*cos(tp[0]-tp[1]))*sin(2.*(tp[0]-tp[1]))/(2.*p0w*p0w);

    vec4 ret;

    ret[0] = (L2*tp[2]-L1*tp[3]*cos(tp[0]-tp[1]))/(L1*p0w);
    ret[1] = (L1*(M1+M2)*tp[3]-L2*M2*tp[2]*cos(tp[0]-tp[1]))/(L2*M2*p0w);
    ret[2] = -(M1+M2)*G*L1*sin(tp[0])-C1+C2;
    ret[3] = -M2*G*L2*sin(tp[1])+C1-C2;

    return ret;    
}

// Move one simulation step forward in the Runge-Kutta(4) method
vec4 step_rk4(in vec4 tp)
{
    vec4 k1 = eval(tp);
    vec4 k2 = eval(tp + h/2.*k1);
    vec4 k3 = eval(tp + h/2.*k2);
    vec4 k4 = eval(tp + h*k3);
    return tp + h/6.*(k1+2.*k2+2.*k3+k4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    h /= float(res);

    if(iFrame == 0 || texelFetch( iChannel1, ivec2(40,1),0 ).x != 0.)
        fragColor = vec4(2.*PI*(uv-.5), 0, 0);
    else
    {
        fragColor = texture(iChannel0, uv);
        for(int i=0; i<res; ++i)
	        fragColor = step_rk4(fragColor);
    }
}

