void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 mouse = iMouse.xy;
    if (iMouse.z < 1.0) {
        mouse = vec2(0.3 * iResolution.x, 0.0);
    }

    vec3 textureSample = texture(iChannel0, uv).rgb;
    float colorSteps = mouse.x / iResolution.x * 10.0;
    textureSample = normalize(textureSample) * (floor(length(textureSample) * colorSteps) / colorSteps);

    fragColor = vec4(textureSample, 1.0);
}
