void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0,uv);
    col += texture(iChannel1, uv)*0.5;

    col = pow(col, vec4(.4545));

    fragColor = col;
}

#define saturate(x) clamp(x,0.,1.)
#define PI 3.14159265

float box(vec3 p) {
    vec3 q = abs(p)-.3;
    return length(max(q,0.)) + min(max(q.x,max(q.y,q.z)), 0.);
}

float map(vec3 p) {
    vec3 pp = p;
    p = mod(p,1.)-.5;
    float d = box(p);
    d = max(d, pp.y-1.);
    return d;
}

vec3 normal(vec3 p) {
    vec2 eps = vec2(0.001, 0.);
    float d = map(p);
    return normalize(vec3(d-map(p-eps.xyy),d-map(p-eps.yxy),d-map(p-eps.yyx)));
}

float rand(float s) {
    return fract(sin(s)*42358.);
}
vec3 albedo(vec3 p) {
    vec2 id = floor(p.xz);
    float s = saturate(cos(length(id)+iTime*2.)*2.-1.);

    vec3 c = vec3(.2,.2,1.) * s;
    if (fract(iTime/13.) > .5) {
    	c = vec3(.1,.2+rand(id.x)*.4,1.)*3. * saturate(rand(id.y+rand(id.x)+floor(iTime*2.)*.1)*9.-8.) * fract(iTime*2.) * (1.-fract(iTime*2.));
    }

    c *= step(0.4, 1.-abs(p.y-.5));
    c *= rand(id.x) + rand(id.y) + .5;
    return c;
}

mat2 rot(float v) {
    float a = cos(v);
    float b = sin(v);
    return mat2(a,b,-b,a);
}

void cameraPath(inout vec3 ro, inout vec3 rd) {
    float t = mod(floor(iTime/10.),5.);

    if (t == 0.) {
    	ro = vec3(-5.*cos(iTime*.1),5., -3.);
        rd.yz = rot(PI*.3) * rd.yz;
    } else if ( t == 2.) {
    	ro = vec3(0.,3.*cos(iTime*.2)+3., iTime*10.);
        rd.xy = rot(iTime*.1) * rd.xy;
        rd.zy = rot(-.8) * rd.zy;
    } else  {
    	ro = vec3(0.,2., -iTime*3.);
        rd.xy = rot(-iTime*.01) * rd.xy;
        rd = -abs(rd);
        rd.xz = rot(iTime*.05) * rd.xz;
        rd = -abs(rd);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.,2., -3.);
    vec3 rd = normalize(vec3(uv*2.-1., 2.-length(uv*2.-1.)*.3));
    cameraPath(ro,rd);
    vec3 p = ro;
    for(int i=0; i<128; i++) {
        p += rd * map(p);
    }
    vec3 n = normal(p);


    vec3 col = vec3(0.);
	col += abs(n.y)*.002;
    col += albedo(p)*2.;
    col += albedo(p+n) * 5. * exp(-length(fract(p)*2.-1.)*4.); // cheap GI :)

    col = mix(col, vec3(0.), saturate(length(p-ro)*0.05));
    fragColor = vec4(max(col,vec3(0.)),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 offset = vec2(0.002, 0.);

	vec4 col = vec4(0.);
    float w = 1.;
    float weight = 0.;
    for (float i=1.; i<20.; i++) {
        w *= .8;
        col += max(vec4(0.), textureLod(iChannel0,uv - offset * i, 0.)-.05) * w;
        col += max(vec4(0.), textureLod(iChannel0,uv + offset * i, 0.)-.05) * w;
        weight += w;
    }
    fragColor = col/weight*.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 offset = vec2(0., 0.002);

	vec4 col = vec4(0.);
    float w = 1.;
    float weight = 0.;
    for (float i=1.; i<20.; i++) {
        w *= .8;
        col += max(vec4(0.), textureLod(iChannel0,uv - offset * i, 0.)) * w;
        col += max(vec4(0.), textureLod(iChannel0,uv + offset * i, 0.)) * w;
        weight += w;
    }
    fragColor = col/weight*.5;
}
