void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    for (float i = 0.; i < N; i++) {
        vec4 a = A(vec2(i,0));
        for (float j = i+1.; j < N; j++) {
            vec4 b = A(vec2(j,0));
            Q += 3./N*exp(-2.*ln(a.xy,b.xy,U));
        }
    }
    Q = .8-Q*vec4(1,.9,.8,1);
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 b = B(U);
    Q.w -= g;
    vec4 dQ = vec4(0);
    for (float i = 0.; i < N; i++) {
    	float L = length(B(vec2(i,0)).xy-b.xy);
        vec2 r = A(vec2(i,0)).xy-Q.xy;
        float l = length(r);
        if (l>0.) {
            vec2 f = normalize(r)*(l-L);
            dQ.xy += Rigid*f;
            dQ.zw += f;
        }
    }
    Q += dQ/N;
    Q.xy += Q.zw;
    if (Q.x<1.)  {Q.x = 1.; Q.zw *= vec2(-1.,.99);}
    if (Q.y<1.)  {Q.y = 1.; Q.zw *= vec2(.5,-1.);}
    if (Q.x>R.x) {Q.x = R.x; Q.zw *= vec2(-1.,.99);}
    if (Q.y>R.y) {Q.y = R.y; Q.zw *= vec2(.99,-1.);}

    if (iMouse.z>0.) {
    	Q.zw -= 10.*(Q.xy-iMouse.xy)/(10.+dot(Q.xy-iMouse.xy,Q.xy-iMouse.xy));

    }

    if (iFrame < 1) {
    	Q.xy = (hash(U+10.+dot(iDate,vec4(1))).xy*0.3+vec2(.25,.5))*R;
        Q.zw = vec2(0);
    }
}

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)

#define N 20.
#define Rigid 1.
#define g (9.8/R.y)

float ln (vec2 a, vec2 b, vec2 u) {
	u = u-a;
    b = b-a;
    return length(u-b*clamp(dot(u,b)/dot(b,b),0.,1.));
}
vec2 hash(vec2 p) // Dave Hoskins
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

