// dual approach of https://www.shadertoy.com/view/wdtXzf

void mainImage( out vec4 O, vec2 u ) {
    vec2  U = u/R;
#if MARGIN
    U = margin( U, *A, -.5*S/R.y );   // restore letters apparent clipping
#endif
    O = texture( iChannel0, U );
}

// utils adapted from from https://www.shadertoy.com/view/llySRh

vec4 char(vec2 p, int c) {
    vec2 dFdx = dFdx(p/16.), dFdy = dFdy(p/16.);
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1);
	return textureGrad( iChannel1, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx, dFdy );
}
#define C(c) char(U,64+c)

#define hash14(p) fract(sin((p)*vec4(127.1,311.7, 269.5,183.3)) *43758.5453123)
#define hue(v)   ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )
//#define rot(a)  mat2(cos( a + vec4(0,33,11,0)))          // from https://www.shadertoy.com/view/XlsyWX 
#define rot(a)    mat2(cos( a + 1.571*vec4(0,1,-1,0)))

void mainImage( out vec4 O, vec2 u )
{
    vec2  U = u/R,
          V = iMouse.z > 0. ? U : margin(U,,DZ),           // dezooming
          W = abs(2.*V-1.);                                // > 1 if out of screen
    V =  (V-.5)*A * rot(.005)  / A + .5;                   // global rotation

    O = max(W.x,W.y) < 1.                              // --- animation 
          ? texture(iChannel0, V)                          // dezoom prev frame
          : vec4(.5+.5*sin(.3*iTime));                     // or background if outside

                                                       // --- draw new letter
    vec4 H = hash14(iTime),                                // 4 random numbers
         C = mix(vec4(1), hue(H.x), fract(8.*H.x) );       // random color
#if MARGIN
    H.yz = margin( H.yz, *A, -.5*S/R.y );                  // prevent letters clipping
#endif
    U = ( U - H.yz ) / S *R * rot(6.28*fract(8.*H.y)) +.5; // random letter location and angle
    float d = C( 1+int(25.*H.w) ).w - .5,                  // SDF to letter 
          w = fwidth(length(U));                           // true pixel width
    O = mix(O, C, smoothstep( w,-w, d) );                  // blend color letter (with AA)
    O = mix(O, vec4(0), smoothstep( w,.0, abs(d)) );       // blend black outline (with AA)
}
