#define luma(O)  dot(O, vec4(.213, .715, .072, 0) )       // luminance

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         M = iMouse.z==0. ? R/2. : iMouse.xy;

    O = texture(iChannel0, u / R );
    O = pow(O, vec4(2.2));                                // from sRGB to flat

    if ( length(u-M) < R.y/3. )                           // around mouse, 
          O[iFrame%3] = O[(iFrame+1)%3] = 0.;             //  decompose colors over time
    else {
        O /= 3.;
        if ( u.y < R.y/4. ) 
            O = iFrame%3 < 1 ? O*3. : vec4(0);            // bottom: paint every 3 frames
      }   


   if ( length(u-R*vec2(.9,.6) ) < R.y/3. )               // right disk
        O[iFrame%3] = luma(O) * 3.,
        O[(iFrame+1)%3] = O[(iFrame+2)%3] = 0.;
    else if ( u.x > R.x/2. ) 
          O = vec4( luma(O) );                            // right: luminance

    O = pow(O, vec4(1./2.2));                             // to sRGB
}
