float N11(float n) {
    return fract(sin(9382.1542*n)*913.542);
}

float N11close(float n) {
    return fract(sin(194.24+n*823.)*2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x /= iResolution.y/iResolution.x;

    float x = uv.x;
    float y = uv.y;

    //Divide [-.5, .5] in 10 regions
    float[30] div;

    for(int i=0; i<div.length(); i++) {
        div[i] = N11(float(i)*877.15)-.5;
    }

    int closestI = 0;
    float minDist = 100.;
    for (int i=0; i<div.length(); i++) {
        float d = abs(div[i]-uv.y);
        if (d < minDist) {
            closestI = i;
            minDist = d;
        }
    }

    float t = iTime*.5;
    bool chance = N11close(t) < mod(t, .2);
    if (chance)
    	x += t*N11(div[closestI]);
    //x += sin(iTime*.3)*uv.y
    float r = 0.;
    if (chance)
        r = t;
    y += cos(r);

    vec4 text = texture(iChannel0, vec2(x, y));

    vec3 col = text.rgb;
    // Output to screen
    fragColor = vec4(col,1.0);
}
