#define EPS 1./iResolution.y
float f(vec2 p) {
    float freq = texture(iChannel1, vec2(0., 0.)).r * 3.;
    float amp = texture(iChannel1, vec2(0.1, 0.)).r ;
    return p.y - 
        sin(freq * 3. * p.x + freq * mod(iTime, 5.))*0.5 * amp
        - cos(freq * 2. * p.x + amp * mod(iTime, 5.))*0.5 * amp;
}

vec2 grad( in vec2 x)
{
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( f(x+h.xy) - f(x-h.xy),
                 f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2. * fragCoord- iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);


    float value = abs(f(uv));
    vec2 g = grad(uv);


    //value = abs(value);
    float d = value/length(g);
    col += smoothstep(10.*EPS, 3.*EPS, d);


    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = mix(texture(iChannel1, uv), texture(iChannel0, uv), 0.1);
}
