#define PHI 1.618033988749895
#define PHI_I 0.618033988749895
#define PI 3.141592653589793
#define TWO_PI 6.283185307179586
#define CELL 8.

float noise(vec2 x)
{
    return fract(cos(dot(x,vec2(1,1.618034)))*416418.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col = vec3(0);
    float n00 = noise(vec2(0,0)-floor(fragCoord.xy/CELL));
    float n01 = noise(vec2(0,1)-floor(fragCoord.xy/CELL));
    float n10 = noise(vec2(1,0)-floor(fragCoord.xy/CELL));
    float n11 = noise(vec2(1,1)-floor(fragCoord.xy/CELL));
    vec2 uvG = fragCoord.xy/CELL;
    uvG = fract(uvG);
    uvG -= 1.;
    float g00 = dot(uvG+vec2(0,0),vec2(sin(n00*TWO_PI),cos(n00*TWO_PI)));
    float g01 = dot(uvG+vec2(0,1),vec2(sin(n01*TWO_PI),cos(n01*TWO_PI)));
    float g10 = dot(uvG+vec2(1,0),vec2(sin(n10*TWO_PI),cos(n10*TWO_PI)));
    float g11 = dot(uvG+vec2(1,1),vec2(sin(n11*TWO_PI),cos(n11*TWO_PI)));
    float size = 2.+sin(iTime);
    g00 = cos(size*g00*TWO_PI);
    g01 = cos(size*g01*TWO_PI);
    g10 = cos(size*g10*TWO_PI);
    g11 = cos(size*g11*TWO_PI);
    uvG += 1.;
    uvG = smoothstep(0.25,.75,uvG);
    float g0 = mix(g01,g00,uvG.y);
    float g1 = mix(g11,g10,uvG.y);
    float g = mix(g1,g0,uvG.x);
    float so = .5+1./(8.+size);
    col = vec3(smoothstep(-so+.25,so+.25,g));


    // Output to screen
    fragColor = vec4(col,1.0);
}
