void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y,
      // M = ( 2.*iMouse.xy - R ) / R.y,
         V = U* ( 1. + (vec2(1,-1)/dot(U,U) ) )         // Z = z + 1/z
           + .1*iTime;
    O = texture(iChannel0,.5+.5*V);                     // texture
 // O = mix(O, vec4(0,0,1,1), max(0.,1.-R.y/4.*abs(length(V-vec2(.5,.1))-1.5)) );
    V = abs(fract(V)-.5) / fwidth(V);                   // grid
    O = mix(O, vec4(1, 0,0,1), max(0.,1.-V.x) * min(1.,4.*length(U)) );
    O = mix(O, vec4(0,.8,0,1), max(0.,1.-V.y) * min(1.,4.*length(U)) );
}
