void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 1.2*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 8.*iMouse.xy/iResolution.xy;
    float pi = 3.14159265359;

    vec2 v = u;
    float t = iTime;
    float r = 2.;
    if(iMouse.z>0.) t = m.x;
    if(iMouse.z>0.) r = m.y*4.-16.;

    for(float i = 0.; i < t; ++i)
    {
        float s = clamp(t-i,0.,1.);
        float e = min(s,.5)*r+1.;
        u = cos(atan(u.y,u.x)*e-vec2(0.,pi*.5))*pow(length(u),e);
        u+= v*max(s-.5,.0)*2.;
    }
    fragColor = vec4(.05/length(u));
    //fragColor = vec4(exp(dot(u,u)*-1.));
    //fragColor = texture( iChannel0, vec2(.125,1.)*.1*u+.4 );
}
