#define SCALE 2.0

#define SPEED 1.0

#define WOBBLING_SPEED 0.9

#define WOBBLING_IMPACT 0.2

#define AIR_PERSPECTIVE 1.0

mat2 rotate2d(float angle){
    return mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );
}

vec2 toCanonicalCoordinates(vec2 pixel) {
    return (pixel -.5 * iResolution.xy) / iResolution.y;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 point = toCanonicalCoordinates(fragCoord);
    vec2 mouse = toCanonicalCoordinates(iMouse.xy);
    point += vec2(0, mouse.y);
    point *= rotate2d(sin(iTime * WOBBLING_SPEED) * WOBBLING_IMPACT);
    vec2 trans = vec2(point.x / point.y, 1.0 / point.y);
    trans *= rotate2d(mouse.x);
    // double press effect
    trans *= SCALE + (sin(iTime * SPEED) * SCALE);
    //vec2 inner = 1.0 - mod(trans + 0.5, 1.0);
    fragColor = texture(iChannel0, trans);
    fragColor *= pow(abs(point.y * 2.0), AIR_PERSPECTIVE);
}
