void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = gl_FragCoord.xy / iResolution.xy;

    // fft < 0.5 < wave    
	float fft  = texture(iChannel1, vec2(uv.x,0.25)).x; 
    float wave = texture(iChannel1, vec2(uv.x,0.75)).x;

    // second texture row is the sound wave

    float mic = texture(iChannel1, vec2(uv.x, uv.y)).x;
    vec4 color = texture(iChannel0, vec2(uv.x-(wave-0.5)*0.01, uv.y-(wave/15.)));


    vec3 rgb = abs(mod(fft*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0;

    fragColor = mix(color, vec4(rgb, 1.0), wave/2.);
}
