float Letter(sampler2D Font,vec2 P,int L)
{
   return texture(Font,(P+vec2(L-(L/16)*16,15-L/16))/16.).x; 	   
}

void mainImage(out vec4 Color, in vec2 Coord)
{
    vec2 U = .5+(Coord-.5*iResolution.xy)/iResolution.y;

    float T = Letter(iChannel0,U,iFrame/5)*step(abs(U.x-.5),.5);
    Color = vec4(smoothstep(.8,.5,T)*vec3(1),1);
}
