float get_weight(vec2 f , float s)
{
    float l=length(f);
    return min(1.0,max(0.0, 3.0-3.0*l/s ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t=iTime;
    if(t<0.5)
    {
        fragColor=vec4(0.0,0.0,0.0,1.0);
        return;
    }
    vec2 f=fragCoord.xy,m=vec2(0.0,0.0);

    float w=0.0;
    w=max(w,get_weight(f-vec2(100,100),100.0));
    w=max(w,get_weight(f-iMouse.xy,100.0));
    w=max(w,get_weight(f-vec2(200,200),100.0));


	fragColor = vec4(0.5,0.5,0.5,0.0)*w+vec4(0.0,0.0,0.0,1.0);

}
