float noise(vec2 uv) {
    vec2 noise = texture(iChannel0, uv).xy;
    float n = .5 + .5 * sin((uv.x * /*-349.099*/noise.x + uv.y * /*324.908*/ noise.y) * 389.2398 * iTime);
	return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	vec3 col = vec3(noise(uv));

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 mainSound( in int samp, float time )
{
    // A 440 Hz wave that attenuates quickly overt time
    return vec2( 2.*sin(6.2831*9.*sin(time*314.235)*time)*exp(-3.0*fract(1.+sin(time*3.)*time)) );
}
