void mainImage( out vec4 Q, in vec2 U )
{
    Q = T(U);
    vec4 b = B(U),
        n = T(U+vec2(0,1)),
        e = T(U+vec2(1,0)),
        s = T(U-vec2(0,1)),
        w = T(U-vec2(1,0));
   Q.x -= .25*(e.z-w.z+Q.w*(n.w-s.w));
   Q.y -= .25*(n.z-s.z+Q.w*(e.w-w.w));
   Q.z  = .25*((s.y-n.y+w.x-e.x)+(n.z+e.z+s.z+w.z));
   Q.w  = .25*((s.x-n.x+w.y-e.y)-(n.w+e.w+s.w+w.w));
        n = B(U+vec2(0,1));
        e = B(U+vec2(1,0));
        s = B(U-vec2(0,1));
        w = B(U-vec2(1,0));
    vec2 g = vec2(e.w-w.w,n.w-s.w)+vec2(e.y-w.y,n.y-s.y);
    Q.xy += 0.1*g;
    Q.x = mix(Q.x,.3,exp(-1e2*length(U-vec2(.1,.5)*R)/R.y));
    vec4 mo = texture(iChannel3,vec2(0));
   	float l = ln(U,mo.xy,mo.zw);
   	if (mo.z > 0. && length(mo.xy-mo.zw)>0.) Q.xy = mix(Q.xy,.03*(mo.xy-mo.zw),smoothstep(10.,0.,l));
    if (U.x < 1. || U.y < 1. || R.x-U.x<1.||R.y-U.y<1.) Q.xy*=0.;
    if (iFrame < 1) {
        Q = vec4(0);
    }
}

