#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
vec4 B (vec2 U) {
    U -= A(U).xy;
    U -= A(U).xy;
    return texture(iChannel1,U/R);
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = B(U);
    #define a 2.
    vec4 n = B(U+vec2(0,a))+B(U+vec2(a,0))+B(U+vec2(0,-a))+B(U+vec2(-a,0));
    n *= .25;
    vec4 dx = n-Q;
    Q += dx*vec4(1,.45,1,.45);

    float x = .3*Q.x*Q.y*(1.-Q.y);
    float y = .3*Q.z*Q.w*(1.-Q.w);

    Q.y = Q.y+x-0.025-.2*n.w+0.001*(1.-n.z);
    Q.x = Q.x+0.1*Q.x*(1.-Q.x)-x;

    Q.w = Q.w+y-0.025-.2*n.y+0.001*(1.-n.x);
    Q.z = Q.z+0.1*Q.z*(1.-Q.z)-y;

    Q = clamp(Q,0.,1.);
    if (iFrame < 1) {
        Q.xz = vec2(1);
        Q.yw = sin(6.2*U.x/R.x*vec2(1,2))+sin(6.2*U.y/R.y*vec2(2,1));
    }
}

