vec3 red = vec3(225./255., 95./255., 60./255.);
vec3 white = vec3(1.,1.,1.);
vec3 blue = vec3(0.,128./255.,1.);
vec3 black = vec3(0.,0.,0.);
vec3 pink = vec3(255./255.,192./255.,203./255.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float x = sin(iTime * 2. ) ;

    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2(.5 * ratio, .5);
    float dist = distance ( shapePos,uv) ;

    uv -= shapePos;

    //get angle
    float angle = atan(uv.y, uv.x) + iTime + 2. * (sin(iTime ));

    float radius = cos(4. * angle + iTime) * .3;
    float radius2 = cos(4. * (atan(uv.y, uv.x) + -iTime * .5) + iTime) * .3;
    float radius3 = cos(4. * angle + iTime) * .3;

    radius = sin (angle * 74.) * sin(angle * 40.) * sin(angle * 8.);
    radius2 = cos (angle * 74.) * cos(angle * 40.) * cos(angle * 8.);
    radius3 = cos (angle * 74.) * cos(angle * 40.) * sin(angle * 8.);

    radius = radius * -x;
    radius2 = radius2 * -x;
    radius3 = radius3 * -x;

    float value = 1. - step(radius,dist * .2);
    float value2 = 1. - step(radius2,dist * .3);
    float value3 = 1. - step(radius3,dist );

    vec3 color = mix(black,red,value);
    color = mix(color,blue,value);
    color = mix(color,red,value2);
    color = mix(color,pink,value3);

    fragColor = vec4(color ,1. );
}
