void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 mouseNormalizedScreenPosition = iMouse.xy / iResolution.xy;

    float distanceToMouse = distance(uv, mouseNormalizedScreenPosition);


    vec3 wantedPixelColor = vec3(0,2,4) * (0.5 - distanceToMouse);

    // Output to screen
    fragColor = vec4(wantedPixelColor,1.0);
}
