void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    fragColor = texture(iChannel0, fragCoord / iResolution.xy);

}

const int SCALE = 3;
const int SIZE = 1024;
const int STOP=810;


float isColor(vec4 c)
{

    if(c.r+c.g+c.b>=0.2){
        return 1.0;
    }
        else {
        return 0.0;
    } 

}

vec4 doRule(float sum){
	vec4 c = vec4(0.,0.,0.,1.0);
	if(sum==1.0)c= vec4(1.0,0.6,0.1,0.0);
	//if(sum==4.0)c= vec4(0.6,0.5,0.2,0.0);
	if(sum==6.0)c= vec4(0.2,0.1,0.6,0.0);
	if(sum==8.0)c= vec4(0.4,0.5,0.2,0.0); 
	return c;   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame<STOP && 1.0==mod(float(iFrame),float(2))){
    fragCoord -= mod(fragCoord, float(SCALE));
	float midx = 0.5*iResolution.x-mod(0.5*iResolution.x,float(SCALE));
    float midy = 0.5*iResolution.y-mod(0.5*iResolution.y,float(SCALE));

    if (iFrame == 1 || iMouse.z > .5)
    {      
        if(fragCoord.x!=midx || fragCoord.y!=midy ){
 		fragColor = vec4(0);
        } else {
         fragColor = vec4(1);   
        }
    }
    else
    {
        if(abs(fragCoord.x-midx)*2.0<float(SIZE*SCALE) && abs(fragCoord.y-midy)*2.0<float(SIZE*SCALE)){
        vec2 o = float(SCALE) / iResolution.xy;
        vec2 uv = (fragCoord / iResolution.xy) + (o * .5);

        float accum = 0.;
        float self = 0.;
        for (int i = -1; i <= 1; ++i)
        {
            for (int j = -1; j <= 1; ++j)
            {
				float s = isColor(texture(iChannel0, uv + o * vec2(i, j)));
                if (i == 0 && j == 0)
                    self = s;
                else
                    accum += s;
            }
        }

        fragColor = vec4(doRule(accum));
        } else {
         fragColor= vec4(0.0,0.0,0.0,0.0);   
        }
    }
    } else {
        fragColor = texture(iChannel0,(fragCoord / iResolution.xy));
    }
}
