#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vec = fragCoord-iResolution.xy*0.5;
    float dist = length(vec);
    float angle = atan(vec.x,vec.y)/(PI*0.25);
    float frac = fract(angle);
    float light = sign(angle-frac+4.0-floor(mod(iTime*10.0,8.0)));
    float a = smoothstep(100.0,97.0,dist)*smoothstep(47.0,50.0,dist)*0.5*smoothstep(.0,.05,frac)*smoothstep(1.0,.95,frac);
    a += a*(-abs(light)+1.0);
    fragColor = vec4(a,a,a,1.0);
}
