float circle(in vec2 _st, in float _radius){
	float d = dot(_st,_st+100.);
    float s = sin(999999.+_radius);
    d = smoothstep(s*(d-.1),s*(d-.1), 1.);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.x/10.;
	vec3 color = vec3(circle(st,0.9),circle(st,0.8),circle(st,0.5));
	fragColor = vec4( color, 1.0 );
}
