void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = 5.0*fragCoord/iResolution.xy;

    for(int n = 1; n < 7; n++){
    float i = float(n);
      p += vec2(0.5 / i * sin(i * p.y  *+ iTime + 0.3 * i) + 0.8, 0.4 / i * sin(i*p.x+ iTime + 0.3 * i) + 1.6);
    }

    p *= vec2(0.5 / tan(p.y + iTime + 0.3) + 0.8, 0.4 / sin(p.x + iTime + 0.3) + 1.6);
    vec3 col = vec3(0.1 * sin(p.x) + 0.2, 0.5 * sin(p.y) + 0.2, sin(p.x));
    col *= 0.9;


    // Output to screen
    fragColor = vec4(col,1.0);
}
