#define SEED int(iTime*3.0)
#define COL vec3(0.3,0.6,0.8)
#define SCALE 256.0
#define REPEAT 256.0
// PATURN 0 or 1 or 2
#define PATURN 2
#define POW 0.5
#define RATE 5.0

#define BitFunc(t,q) (t&63^t<<q*(t&7)) |(t&63|t*52<<q*(t&8)) ^ (t*16*(t&63|t<<q*(t&15)))
//#define BitFunc(t,q) (t&63|t*52<<q*(t&8)) ^ (t*16*(t&63|t<<q*(t&15)))
//#define BitFunc(t,q) (t&63|t*52<<q*(t&8))
//#define BitFunc(t,q) (t*16*(t&63|t<<q*(t&15)))
//#define BitFunc(t,q) t&63^t<<q*(t&7)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
	uv*=SCALE;
    uv=mod(uv,REPEAT)-0.5*REPEAT;
	ivec2 p = ivec2(pow(abs(uv),vec2(POW))*RATE);
    int q = int[](p.x|p.y, p.x&p.y, p.x^p.y)[PATURN];
    int T=BitFunc(SEED,q);
    float x = float((T>>2&15)+(T>>4&15)+(T>>8&15))/16.0;
    vec3 col = COL*fwidth(x);
    fragColor = vec4(col,1);
}
