void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);

    float aud = texture(iChannel0, vec2(0.0)).x;
    fragColor *= aud;
}

vec4 drawHeart(vec2 p) {
    // from iq https://www.shadertoy.com/view/XsfGRn
    float tt = mod(iTime,1.5)/1.5;
    float ss = pow(tt,.2)*0.5 + 0.5;
    ss = 1.0 + ss*0.5*sin(tt*6.2831*3.0 + p.y*0.5)*exp(-tt*4.0);
    p *= vec2(0.5,1.5) + ss*vec2(0.5,-0.5);
    p.y -= 0.25;
    float a = atan(p.x,p.y)/3.141593;
    float r = length(p);
    float h = abs(a);
    float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);
    float s = 0.75 + 0.75*p.x;
	s *= 1.0-0.4*r;
	s = 0.3 + 0.7*s;
	s *= 0.5+0.5*pow( 1.0-clamp(r/d, 0.0, 1.0 ), 0.1 );
	vec3 hcol = vec3(1.0,0.5*r,0.3)*s;
	float aa = smoothstep( -0.01, 0.01, d-r);
   	return vec4(hcol, aa);
}

vec4 drawGabbyFlower(vec2 p) {
    float r = length(p);
    float theta = atan(p.x, p.y) + 0.265;

    if (r < 0.2)
        return vec4(140.0/256.0, 128.0/256.0, 0.0, 1.0); // Center

    if (r < abs(sin(theta*6.0)))
        return vec4(1.0, 1.0, 100.0/256.0, 1.0); // Petal

    if (p.y < 0.0 && abs(p.x) < 0.05)
        return vec4(100.0/256.0, 180.0/256.0, 50.0/256.0, 1.0); // Stem

    return vec4(0.0, 0.0, 0.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv * 2.0 - 1.0;
    p.x *= iResolution.x/iResolution.y;

    vec4 hcol = drawHeart(p*2.0 + vec2(0.0, -0.1));

    vec2 warp = (uv - vec2(0.5, 0.4)) * pow(0.9, iTimeDelta);
    warp += vec2(0.5, 0.4);
    float fade = pow(0.9, iTimeDelta);
    vec3 bcol = vec3(0.2, 0.4, 0.0);
    if (uv.y > 0.5) bcol = vec3(0.2, 0.4, 0.8);
    vec3 col = texture(iChannel0, warp).rgb*fade + (1.0 - fade) * bcol;

    p.x += floor(6.0*(texture(iChannel1, vec2(0.1, 0.5)).x - 0.5) * 6.0)/6.0;
    p.y += floor(6.0*(texture(iChannel1, vec2(0.5, 0.5)).x - 0.5) * 1.0)/6.0;
    vec4 f = vec4(0.0);
    if (iFrame % 10 == 0)
    	f = drawGabbyFlower(p*2.5);

    col = mix(col, hcol.rgb, hcol.a);
    if (hcol.a < 0.5)
    	col = mix(col, f.rgb, f.a);



    // Output to screen
    fragColor = vec4(col,1.0);

    if (fragCoord.x < 3.0 && fragCoord.y < 3.0)
        fragColor = vec4(texture(iChannel1, vec2(0.1, 0.5)).x, 0.0, 0.0, 1.0);
}
