#define  PI 3.142857142
#define DPI 6.285714285

float Circle (vec2 uv, float blur, float r) {
    float d = length(uv);
    float c = smoothstep(r,r-blur,d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(0.0);
    vec2 pos= vec2(-1.,0.);

    uv += pos;

    pos=vec2(-0.1+sin(iTime)/2.,0.5);
	float c0 = Circle(uv -pos,.001,.1);
    pos=vec2(-.1,0.5+sin(iTime)/2.);
    float c1 = Circle(uv -pos,.001,.1);

    col += vec3(c0)*vec3(0,0.5,1);
    col += vec3(c1)*vec3(0,0.5,1);
    // Output to screen
    fragColor = vec4(col,1.0);
}
