void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float x=sin(uv.x)*sin(uv.y);
   	vec2 center = vec2(.5,.5);

    float d=distance(center,uv.xy);
    float randomtexture = .4;//may be Random color
    d=(1.-d*2.0)*pow(randomtexture,d*.5);

    float angle=atan((uv.y-.5)/(uv.x-.5));
    angle=degrees(angle); //convert angle
    angle+=270.; //Move angle to (0,360)
    if(d>.15)
    {
    angle=mod(angle,360.*d*pow(sin(iTime), 2.));
    d=(d)*(angle);
    d/=2.;//scale the density
    }
    // Output to screen
    fragColor = vec4(vec3(d),1.0)*texture(iChannel0, uv);
}
