void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float f = texture(iChannel0, vec2(0.5)).r;
    vec3 blr = vec3(0.0);
    float numf = 30.0; // sin(iTime * 0.2) * 10.0 + 12.0;
    vec2 uv2 = uv - 0.5;
    float dist = clamp(length(uv2 * 2.0), 0.0, 1.0);
    for (float n = 0.0; n < numf; n += 1.0) {
        vec3 t = texture(iChannel0, uv2 + 0.5).rgb;
        blr -= smoothstep(0.4, 0.0, t) / numf * 0.95;
        uv2 *= 1.0 + 0.5 / numf * (f - 0.5);
        float a = (f - 0.5) * 2.0 / numf * (1.0 - dist);
        uv2 *= mat2(cos(a), -sin(a), sin(a), cos(a));
    }
    //blr /= numf;
	blr += texture(iChannel0, uv).rgb;

    vec3 old = texture(iChannel1, uv).rgb;

    vec3 ret = blr;



    fragColor = vec4(ret, 1.0);
}
