void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec4 webcam = texture(iChannel0, uv);

    vec2 pos = vec2(1.5*sin(iTime*0.5),1.5*cos(iTime*0.75));

    uv -= pos;
    uv *= 1.0125;
    uv += pos;

    vec4 buffer = texture(iChannel1, uv);


    fragColor = (buffer*0.75 + webcam*0.25);
